package bean;

public class Sachbean {
	private String masach;
	private String tensach;
	private Long soluong;
	private String tacgia;
	private Long gia;
	private String sotap;
	private String anh;
	private String ngaynhap;
	private String maloai;
	public Sachbean() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Sachbean(String masach, String tensach, Long soluong, String tacgia, Long gia, String sotap, String anh,
			String ngaynhap, String maloai) {
		super();
		this.masach = masach;
		this.tensach = tensach;
		this.soluong = soluong;
		this.tacgia = tacgia;
		this.gia = gia;
		this.sotap = sotap;
		this.anh = anh;
		this.ngaynhap = ngaynhap;
		this.maloai = maloai;
	}
	public String getMasach() {
		return masach;
	}
	public void setMasach(String masach) {
		this.masach = masach;
	}
	public String getTensach() {
		return tensach;
	}
	public void setTensach(String tensach) {
		this.tensach = tensach;
	}
	public Long getSoluong() {
		return soluong;
	}
	public void setSoluong(Long soluong) {
		this.soluong = soluong;
	}
	public String getTacgia() {
		return tacgia;
	}
	public void setTacgia(String tacgia) {
		this.tacgia = tacgia;
	}
	public Long getGia() {
		return gia;
	}
	public void setGia(Long gia) {
		this.gia = gia;
	}
	public String getSotap() {
		return sotap;
	}
	public void setSotap(String sotap) {
		this.sotap = sotap;
	}
	public String getAnh() {
		return anh;
	}
	public void setAnh(String anh) {
		this.anh = anh;
	}
	public String getNgaynhap() {
		return ngaynhap;
	}
	public void setNgaynhap(String ngaynhap) {
		this.ngaynhap = ngaynhap;
	}
	public String getMaloai() {
		return maloai;
	}
	public void setMaloai(String maloai) {
		this.maloai = maloai;
	}
	
}
